function scroll_to(clicked_link, nav_height) {
    var element_class = clicked_link.attr("href").replace("#", "."), scroll_to = 0;
    ".top-content" != element_class && (element_class += "-container", scroll_to = $(element_class).offset().top - nav_height), $(window).scrollTop() != scroll_to && $("html, body").stop().animate({scrollTop: scroll_to}, 1e3)
}

jQuery(document).ready((function () {
    function scroll_to(clicked_link, nav_height) {
        var element_class = clicked_link.attr("href").replace("#", "."), scroll_to = 0;
        ".top-content" != element_class && (element_class += "-container", scroll_to = $(element_class).offset().top - nav_height), $(window).scrollTop() != scroll_to && $("html, body").stop().animate({scrollTop: scroll_to}, 1e3)
    }

    $("#camp-slider .carousel-inner .carousel-item").first().addClass("active"), $("a.scroll-link").on("click", (function (e) {
        e.preventDefault(), scroll_to($(this), $("nav").outerHeight())
    })), $("#camp-slider").on("slide.bs.carousel", (function (e) {
        var $e, idx = $(e.relatedTarget).index(), itemsPerSlide = 2, totalItems = $(".carousel-item").length;
        if (idx >= totalItems - 1) for (var it = 4 - (totalItems - idx), i = 0; i < it; i++) "left" == e.direction ? $(".carousel-item").eq(i).appendTo(".carousel-inner") : $(".carousel-item").eq(0).appendTo(".carousel-inner")
    }))
}));
